# Scaling function - I use this a lot in models. We want to 'scale' the covariates

scale2 &lt;- function(x) {
   mx &lt;- x - mean(x)
   sdx &lt;- sd(x) 

   if (sdx == 0) {
      out &lt;- mx
   } else {
      out &lt;- mx/sdx
   } 
   return(out)
}
